	var bidinc = 1;
 
	
	var minbidamount = parseFloat(ecp) + parseFloat(bidinc);
     	
   	if(bidprice > ecp){	
	
	 
		if(bidprice < minbidamount){
		
			alert("Please enter a value greater than: £"+minbidamount);
			return false;
		
		}
	
	
   	
   	}else{
