var pptMembersArea = function(){


	var screenWidth = jQuery( window ).width();
	  
	
	var handleTheme = function(){
	 
		jQuery('#ppt-account-wrapper').addClass('show');
	}
	
	var handleMetisMenu = function() {
	 
		if(jQuery('#menu').length > 0 ){
			jQuery("#menu").metisMenu();
		}
		jQuery('.metismenu > .mm-active ').each(function(){
			if(!jQuery(this).children('ul').length > 0)
			{
				jQuery(this).addClass('active-no-child');
			}
		});
	}
	
	var handleAllChecked = function() {
		jQuery("#checkAll").on('change',function() {
			jQuery("td input:checkbox").prop('checked', jQuery(this).prop("checked"));
		});
	}
	
	var handleNavigation = function() {
		jQuery(".nav-control").on('click', function() {

			jQuery('#ppt-account-wrapper').toggleClass("menu-toggle");

			jQuery(".menubars").toggleClass("is-active"); 
				
		});
	}
	
	var handleCurrentActive = function() {
		for (var nk = window.location,
			o = jQuery("ul#menu a").filter(function() {
				return this.href == nk;
			})
			.addClass("mm-active")
			.parent()
			.addClass("mm-active");;) {
	 
		if (!o.is("li")) break;
			o = o.parent()
				.addClass("mm-show")
				.parent()
				.addClass("mm-active");
		}
	}
 
	var handleMiniSidebar = function() {
		jQuery("ul#menu>li").on('click', function() {
			const sidebarStyle = jQuery('body').attr('data-sidebar-style');
			if (sidebarStyle === 'mini') {
				console.log(jQuery(this).find('ul'))
				jQuery(this).find('ul').stop()
			}
		})
	}
	
	var handleMinHeight = function() {
		var win_h = window.outerHeight;
		var win_h = window.outerHeight;
		if (win_h > 0 ? win_h : screen.height) {
			jQuery(".content-body").css("min-height", (win_h + 60) + "px");
		};
	}
 
	
	var handleHeaderHight = function() {
		const headerHight = jQuery('.header').innerHeight();
		jQuery(window).scroll(function() {
			if (jQuery('body').attr('data-layout') === "horizontal" && jQuery('body').attr('data-header-position') === "static" && jQuery('body').attr('data-sidebar-position') === "fixed")
				jQuery(this.window).scrollTop() >= headerHight ? jQuery('.ppt-account-nav').addClass('fixed') : jQuery('.ppt-account-nav').removeClass('fixed')
		});
	}
	
	var handleDzScroll = function() {
		jQuery('.dz-scroll').each(function(){
		
			var scroolWidgetId = jQuery(this).attr('id');
			const ps = new PerfectScrollbar('#'+scroolWidgetId, {
			  wheelSpeed: 2,
			  wheelPropagation: true,
			  minScrollbarLength: 20
			});
		})
	}
	
	var handleMenuTabs = function() {
		if(screenWidth <= 991 ){
			jQuery('.menu-tabs .nav-link').on('click',function(){
				if(jQuery(this).hasClass('open'))
				{
					jQuery(this).removeClass('open');
					jQuery('.fixed-content-box').removeClass('active');
					jQuery('.menubars').show();
				}else{
					jQuery('.menu-tabs .nav-link').removeClass('open');
					jQuery(this).addClass('open');
					jQuery('.fixed-content-box').addClass('active');
					jQuery('.menubars').hide();
				} 
			});
			jQuery('.close-fixed-content').on('click',function(){
				jQuery('.fixed-content-box').removeClass('active');
				jQuery('.menubars').removeClass('is-active');
				jQuery('#ppt-account-wrapper').removeClass('menu-toggle');
				jQuery('.menubars').show();
			});
		}
	}
	 
	
	var handlePerfectScrollbar = function() {
		if(jQuery('.ppt-account-nav-scroll').length > 0)
		{
			const qs = new PerfectScrollbar('.ppt-account-nav-scroll');
		}
	}
 
	return {
		init:function(){
		 
			handleTheme();
			handleMetisMenu();	
			handleAllChecked();	
			handleNavigation();
			handleCurrentActive(); 
			handleMiniSidebar();
			handleMinHeight();
	 
			handleHeaderHight();
			handleDzScroll();
			handleMenuTabs();
		  
			handlePerfectScrollbar();
			
		}, 
		load:function(){
		  
			handleTheme();
			
			_docsLoadPage("p_intro");
			
		},
		
		resize:function(){ 
			
		}
	}
	
}();

 
jQuery(document).ready(function() {
    'use strict';
 
	
	pptMembersArea.init();
	
	function getUrlParams(dParam) {
		var dPageURL = window.location.search.substring(1),
			dURLVariables = dPageURL.split('&'),
			dParameterName,
			i;

		for (i = 0; i < dURLVariables.length; i++) {
			dParameterName = dURLVariables[i].split('=');

			if (dParameterName[0] === dParam) {
				return dParameterName[1] === undefined ? true : decodeURIComponent(dParameterName[1]);
			}
		}
	}
	
	var direction =  getUrlParams('dir');
	if(direction != 'rtl')
	{direction = 'ltr'; }

	var pptConfigOptions = {
		layout: "Vertical", 
		sidebarStyle: "full",
		sidebarPosition: "fixed",
		headerPosition: "fixed",	 
		direction: direction
	};
		
	new pptConfig(pptConfigOptions); 

	jQuery(window).on('resize',function(){
		new pptConfig(pptConfigOptions); 
	});
	
	
});

jQuery(window).on('load',function () {
	'use strict'; 
	pptMembersArea.load();
	
}); 
jQuery(window).on('resize',function () {
	'use strict'; 
	pptMembersArea.resize();
}); 


var body = jQuery('body');
var html = jQuery('html');

function pptConfig({ layout, sidebarStyle, sidebarBg, sidebarPosition, headerPosition,  direction, primary}) { 
 
    this.layout = layout || "vertical";
    this.sidebarStyle = sidebarStyle || "full";
    this.sidebarPosition = sidebarPosition || "static";
    this.headerPosition = headerPosition || "static";
    this.direction = direction || "ltr";
    this.manageLayout(); 
    this.manageSidebarStyle();  
    this.manageSidebarPosition();
    this.manageHeaderPosition();
    this.manageRtlLayout();
    this.manageResponsiveSidebar(); 

} 
 
pptConfig.prototype.manageLayout = function() {
    switch(this.layout) {
        case "horizontal": 
            this.sidebarStyle === "overlay" ? body.attr("data-sidebar-style", "full") : body.attr("data-sidebar-style", `${this.sidebarStyle}`);
            body.attr("data-layout", "horizontal");
            break;
        case "vertical": 
            body.attr("data-layout", "vertical");
            break;
        default:
            body.attr("data-layout", "vertical");
    }
} 

pptConfig.prototype.manageSidebarStyle = function() {

    switch(this.sidebarStyle) {
        case "full":
            body.attr("data-sidebar-style", "full");
            break;
        case "mini":
            body.attr("data-sidebar-style", "mini");
            break;  
        case "overlay":
            this.layout === "horizontal" ? body.attr("data-sidebar-style", "full") : body.attr("data-sidebar-style", "overlay");
            break;
        default:
            body.attr("data-sidebar-style", "full");
    }
} 

pptConfig.prototype.manageSidebarPosition = function() {
    switch(this.sidebarPosition) {
        case "fixed": 
            this.sidebarStyle === "overlay" && this.layout === "vertical" || this.sidebarStyle === "modern" ? body.attr("data-sidebar-position", "static") : body.attr("data-sidebar-position", "fixed");
            break;
        case "static": 
            body.attr("data-sidebar-position", "static");
            break;
        default: 
            body.attr("data-sidebar-position", "static");       
    }
}

pptConfig.prototype.manageHeaderPosition = function() {
    switch(this.headerPosition) {
        case "fixed": 
            body.attr("data-header-position", "fixed");
            break;
        case "static": 
            body.attr("data-header-position", "static");
            break;
        default: 
            body.attr("data-header-position", "static");       
    }
} 

pptConfig.prototype.manageRtlLayout = function() {
    switch(this.direction) {
        case "rtl":
            html.attr("dir", "rtl");
            html.addClass('rtl');
            body.attr("direction", "rtl");
            break;
        case "ltr": 
            html.attr("dir", "ltr");
            html.removeClass('rtl');
            body.attr("direction", "ltr");
            break;
        default: 
            html.attr("dir", "ltr");
            body.attr("direction", "ltr");
    }
}

pptConfig.prototype.manageResponsiveSidebar = function() {
    const innerWidth = jQuery(window).innerWidth();
    if(innerWidth < 1200) {
        body.attr("data-layout", "vertical");
    }

    if(innerWidth > 767 && innerWidth < 1200) {
        body.attr("data-sidebar-style", "mini");
    }

    if(innerWidth < 768) {
        body.attr("data-sidebar-style", "overlay");
    }
}
 


